package com.laddu.react_fragment;

import android.annotation.SuppressLint;
import android.app.Dialog;
import android.app.DialogFragment;
import android.content.Context;
import android.content.res.TypedArray;
import android.graphics.Point;
import android.os.Bundle;
import android.support.annotation.Nullable;
import android.view.Gravity;
import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;
import android.view.Window;
import android.view.WindowManager;


/**
 * Created by Amar Jain on 07/08/16.
 */
@SuppressLint("ValidFragment")
public class BaseDialogFragment extends DialogFragment {

    private static final String TAG = BaseDialogFragment.class.getSimpleName();
    private View mView;

    public void setView(View view) {
        this.mView = view;
    }

    @Override
    public Dialog onCreateDialog(Bundle savedInstanceState) {
        final Dialog dialog = super.onCreateDialog(savedInstanceState);
        dialog.getWindow().getAttributes().windowAnimations = R.style.rate_dialog_slide_animation;
        dialog.requestWindowFeature(Window.FEATURE_NO_TITLE);
        dialog.getWindow().setBackgroundDrawableResource(R.drawable.background_top_rounded_corner);
        dialog.getWindow().setLayout(ViewGroup.LayoutParams.MATCH_PARENT, ViewGroup.LayoutParams.MATCH_PARENT);
        return dialog;
    }

    @Nullable
    @Override
    public View onCreateView(LayoutInflater inflater, ViewGroup container, Bundle savedInstanceState) {
        return mView;
    }

    @Override
    public void onResume() {
        doDialogLayout();
        super.onResume();
    }

    private void doDialogLayout() {
        Window window = getDialog().getWindow();
        window.setLayout(getDialogWidth(), getDialogHeight());
        window.setGravity(Gravity.BOTTOM);
    }

    /**
     * By default, (Screen Height - Action Bar Height) is set as dialog height
     *
     * @return Height of the dialog
     */
    public Integer getDialogHeight() {
        Point size = new Point();
        ((WindowManager)getActivity().getSystemService(Context.WINDOW_SERVICE)).getDefaultDisplay().getSize(size);
        TypedArray styledAttributes = getActivity().getTheme().obtainStyledAttributes(
                new int[]{android.R.attr.actionBarSize});
        int actionBarSize = (int) styledAttributes.getDimension(0, 0);
        styledAttributes.recycle();
        return size.y - actionBarSize;
    }

    /**
     * By default, Screen width is set as dialog width
     *
     * @return Width of the dialog
     */
    public Integer getDialogWidth() {
        Point size = new Point();
        ((WindowManager)getActivity().getSystemService(Context.WINDOW_SERVICE)).getDefaultDisplay().getSize(size);
        return size.x;
    }
}