package com.laddu.react_fragment;

import android.app.DialogFragment;
import android.app.FragmentManager;
import android.os.Bundle;

import com.facebook.react.bridge.Callback;

import javax.annotation.Nullable;

public class FragmentManagerHelper {

    private final FragmentManager mFragmentManager;

    public FragmentManagerHelper(FragmentManager fragmentManager) {
        mFragmentManager = fragmentManager;
    }

    public void showDialog(BaseDialogFragment fragment){
        fragment.show(mFragmentManager, fragment.getClass().getSimpleName());
    }

    public void dismissDialog(BaseDialogFragment fragment){
        fragment.dismiss();
    }
}