package com.laddu.react_fragment;

import android.app.Activity;
import android.content.DialogInterface;
import android.os.Bundle;
import android.support.v4.app.FragmentActivity;
import android.view.View;

import com.facebook.common.logging.FLog;
import com.facebook.react.bridge.Callback;
import com.facebook.react.bridge.LifecycleEventListener;
import com.facebook.react.bridge.ReactApplicationContext;
import com.facebook.react.bridge.ReactContextBaseJavaModule;
import com.facebook.react.bridge.ReactMethod;
import com.facebook.react.bridge.ReadableArray;
import com.facebook.react.bridge.ReadableMap;
import com.facebook.react.common.MapBuilder;

import java.util.Map;

import javax.annotation.Nullable;

/**
 * Created by Amar Jain on 07/08/16.
 */
public class ReactDialogFragmentModule extends ReactContextBaseJavaModule implements LifecycleEventListener {

    static final String NAME = "ReactDialogFragmentModule";
    private BaseDialogFragment baseDialogFragment;

    public ReactDialogFragmentModule(ReactApplicationContext reactContext) {
        super(reactContext);
        baseDialogFragment = new BaseDialogFragment();
    }

    @Override
    public String getName() {
        return NAME;
    }

    @Override
    public void initialize() {
        getReactApplicationContext().addLifecycleEventListener(this);
    }

    @Override
    public void onHostPause() {
        baseDialogFragment.onPause();
    }

    @Override
    public void onHostDestroy() {
        baseDialogFragment.onDestroy();
    }

    @Override
    public void onHostResume() {
        baseDialogFragment.onResume();
    }

    public void show(View view) {
        baseDialogFragment.setView(view);
        FragmentManagerHelper fragmentManagerHelper = getFragmentManagerHelper();
        if(fragmentManagerHelper != null) {
            fragmentManagerHelper.showDialog(baseDialogFragment);
        }
    }

    public void dismiss(){
        FragmentManagerHelper fragmentManagerHelper = getFragmentManagerHelper();
        if(fragmentManagerHelper != null){
            fragmentManagerHelper.dismissDialog(baseDialogFragment);
        }
    }

    private
    @Nullable
    FragmentManagerHelper getFragmentManagerHelper() {
        Activity activity = getCurrentActivity();
        if (activity == null) {
            return null;
        }
        return new FragmentManagerHelper(activity.getFragmentManager());
    }
}
